// RestaurantDetails.js
import React from 'react';
import { useLocation } from 'react-router-dom';

function RestaurantDetails() {
  const location = useLocation();
  const { name, description, price, meal_type } = location.state.restaurant;

  return (
    <div style={styles.detailsContainer}>
      <h1 style={styles.detailsTitle}>{name}</h1>
      <p style={styles.detailsDescription}>{description}</p>
      <p style={styles.detailsPrice}>Price: {price}</p>
      <p style={styles.detailsMealType}>Meal Type: {meal_type}</p>
    </div>
  );
}

const styles = {
  detailsContainer: {
    padding: '20px',
    textAlign: 'center',
  },
  detailsTitle: {
    fontSize: '30px',
    fontWeight: 'bold',
    marginBottom: '10px',
  },
  detailsDescription: {
    fontSize: '18px',
    marginBottom: '10px',
  },
  detailsPrice: {
    fontSize: '16px',
    color: '#333',
  },
  detailsMealType: {
    fontSize: '16px',
    color: '#555',
  },
};

export default RestaurantDetails;
