import React, { useState } from 'react';
import axios from 'axios';
import logo from './Dineology_logo.jpeg'; // Asegúrate de tener el logo en la carpeta src

function App() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);

  const handleSearch = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:9200/restaurants/_search', {
        query: {
          multi_match: {
            query: query,
            fields: ['name', 'meal_type', 'description'],
          },
        },
      });
      setResults(response.data.hits.hits);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  return (
    <div className="App" style={styles.container}>
      <img src={logo} alt="Logo" style={styles.logo} />
      <form onSubmit={handleSearch} style={styles.form}>
        <input
          type="text"
          placeholder="Search for restaurants..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          style={styles.input}
        />
        <button type="submit" style={styles.button}>Search</button>
      </form>

      <div style={styles.resultsGrid}>
        {results.map((result) => (
          <div key={result._id} style={styles.resultCard}>
            <img
              src={result._source.restaurant_photo_url}
              alt={result._source.name}
              style={styles.restaurantPhoto}
            />
            <div style={styles.resultInfo}>
              <span style={styles.newBadge}>NUEVO</span>
              <h2 style={styles.resultName}>{result._source.name}</h2>
              <p style={styles.resultLocation}>Madrid, España</p>
              <p style={styles.resultPrice}>{result._source.price}</p>
              <p style={styles.resultMealType}>{result._source.meal_type}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

const styles = {
  container: {
    textAlign: 'center',
    padding: '50px',
    backgroundColor: '#f5f5f5',
  },
  logo: {
    width: '300px',
    marginBottom: '30px',
  },
  form: {
    display: 'flex',
    justifyContent: 'center',
    marginBottom: '20px',
  },
  input: {
    width: '400px',
    padding: '10px',
    fontSize: '16px',
    border: '1px solid #ccc',
    borderRadius: '5px',
  },
  button: {
    padding: '10px 20px',
    fontSize: '16px',
    marginLeft: '10px',
    border: 'none',
    borderRadius: '5px',
    backgroundColor: '#4285f4',
    color: '#fff',
    cursor: 'pointer',
  },
  resultsGrid: {
    display: 'flex',
    justifyContent: 'center',
    flexWrap: 'wrap',
    gap: '20px',
    maxWidth: '1200px',
    margin: '0 auto',
  },
  resultCard: {
    width: '250px',
    border: '1px solid #ccc',
    borderRadius: '10px',
    overflow: 'hidden',
    boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
    textAlign: 'center',
  },
  restaurantPhoto: {
    width: '100%',
    height: '200px',
    objectFit: 'cover',
  },
  resultInfo: {
    padding: '15px',
    textAlign: 'left',
  },
  newBadge: {
    display: 'inline-block',
    backgroundColor: '#FF0000',
    color: '#fff',
    fontSize: '12px',
    padding: '2px 6px',
    borderRadius: '3px',
    marginBottom: '10px',
  },
  resultName: {
    fontSize: '18px',
    fontWeight: 'bold',
    margin: '5px 0',
  },
  resultLocation: {
    fontSize: '14px',
    color: '#777',
    margin: '5px 0',
  },
  resultPrice: {
    fontSize: '14px',
    fontWeight: 'bold',
    color: '#333',
    margin: '5px 0',
  },
  resultMealType: {
    fontSize: '14px',
    color: '#555',
    margin: '5px 0',
  },
};

export default App;
