// components/RestaurantDetails.js
import React from 'react';
import { useLocation, useParams } from 'react-router-dom';
import './RestaurantDetails.css';

function RestaurantDetails() {
  const location = useLocation();
  const { restaurantName } = useParams();
  const restaurant = location.state?.restaurant;

  if (!restaurant) {
    return <p>Datos del restaurante no disponibles.</p>;
  }

  return (
    <div className="detailsContainer">
      <div className="restaurantCard">
        <img src={restaurant.restaurant_photo_url || "default-image.jpg"} alt={restaurant.name} className="restaurantImage" />
        <div className="restaurantInfo">
          <h1 className="detailsTitle">{restaurant.name}</h1>
          <p className="detailsDescription">{restaurant.description || "No hay descripción disponible"}</p>
          <div className="detailsData">
            <p className="detailsPrice">Precio: {restaurant.price}</p>
            <p className="detailsMealType">Tipo de comida: {restaurant.meal_type.join(', ')}</p>
            <p className="detailsRating">Estrellas: {restaurant.star_number} ⭐</p>
            <p className="detailsContact">Contacto: {restaurant.contact_number}</p>
            <p className="detailsDirection">Dirección: {restaurant.direction || "No disponible"}</p>
            <a href={restaurant.web_url} className="detailsWeb" target="_blank" rel="noopener noreferrer">Visitar Web</a>
          </div>
        </div>
      </div>
      <div className="workingSchedule">
        <h2>Horario de trabajo</h2>
        <ul>
          {Object.entries(restaurant.working_schedule).map(([day, hours]) => (
            <li key={day}>
              {day}: {hours[0]}
            </li>
          ))}
        </ul>
      </div>
      <div className="restaurantServices">
        <h2>Servicios</h2>
        <ul>
          {restaurant.restaurant_services.map((service, index) => (
            <li key={index}>{service}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default RestaurantDetails;
