// components/RestaurantDetails.js
import React from 'react';
import { useLocation, useParams } from 'react-router-dom';
import './RestaurantDetails.css'; // Asegúrate de crear un archivo CSS para el estilo

function RestaurantDetails() {
  const location = useLocation();
  const { restaurantName } = useParams();
  const restaurant = location.state?.restaurant;

  if (!restaurant) {
    return <p>Datos del restaurante no disponibles.</p>;
  }

  return (
    <div className="detailsContainer">
      <img src={restaurant.image || "default-image.jpg"} alt={restaurant.name} className="restaurantImage" />
      <h1 className="detailsTitle">{restaurant.name}</h1>
      <p className="detailsDescription">{restaurant.description || "No hay descripción disponible"}</p>
      <div className="detailsInfo">
        <p className="detailsPrice">Price: {restaurant.price}</p>
        <p className="detailsMealType">Meal Type: {restaurant.meal_type}</p>
        {/* Agrega más detalles como la puntuación */}
        <p className="detailsRating">Rating: {restaurant.rating || "No rating available"}</p>
      </div>
    </div>
  );
}

export default RestaurantDetails;
