// App.js
import React, { useState, useEffect } from 'react';
import { fetchResultsFromAPI } from './services/api'; // Asegúrate de que la importación sea correcta

const App = () => {
  const [results, setResults] = useState([]);
  const [mealTypes, setMealTypes] = useState([]);
  const [priceRanges, setPriceRanges] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');

  // Este efecto se ejecuta cada vez que se cambia el `searchQuery`
  useEffect(() => {
    const fetchData = async () => {
      try {
        // Llamada a la API para obtener los resultados
        const { processedHits, uniqueMealTypes, priceRanges } = await fetchResultsFromAPI(searchQuery);
        setResults(processedHits); // Guardamos los restaurantes en el estado
        setMealTypes(uniqueMealTypes); // Guardamos los tipos de comida
        setPriceRanges(priceRanges); // Guardamos los rangos de precio
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    if (searchQuery) {
      fetchData(); // Llamar a la función solo si hay algo que buscar
    }
  }, [searchQuery]);

  return (
    <div className="App">
      <header className="App-header">
        <h1>Restaurants Search</h1>
        <input
          type="text"
          placeholder="Search for a restaurant"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)} // Actualizamos el estado cuando cambia el texto
        />
      </header>

      <div className="resultsGrid">
        {results.map(restaurant => (
          <div key={restaurant._id} className="restaurantItem">
            <h2>{restaurant._source.name}</h2>
            <p>{restaurant._source.price}</p>
            <p>{restaurant._source.meal_type.join(', ')}</p>
          </div>
        ))}
      </div>

      <div className="filters">
        <h3>Meal Types:</h3>
        <ul>
          {mealTypes.map(meal => (
            <li key={meal}>{meal}</li>
          ))}
        </ul>
        <h3>Price Ranges:</h3>
        <ul>
          {priceRanges.map(price => (
            <li key={price}>{price}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default App;
