import React, { useState, useEffect } from 'react';
import { Routes, Route, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './App.css';
import logo from './Dineology_logo.jpeg';
import RestaurantDetails from './components/RestaurantDetails';

function App() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [notFound, setNotFound] = useState(false);
  const [filters, setFilters] = useState({
    starsOrSoles: 'both', // "soles", "estrellas", "both"
    community: '',
    priceRange: '',
    mealType: '',
    starNumber: '',
  });
  const navigate = useNavigate();

  const handleSearch = async (e) => {
    e.preventDefault();
    fetchResults();
  };

  const fetchResults = async () => {
    try {
      const response = await axios.post('http://localhost:9200/restaurants/_search', {
        query: {
          bool: {
            must: {
              multi_match: {
                query: query,
                fields: ['name', 'meal_type'],
              },
            },
            filter: [
              { term: { starsOrSoles: filters.starsOrSoles } },
              { term: { community: filters.community } },
              { term: { priceRange: filters.priceRange } },
              { term: { mealType: filters.mealType } },
              { term: { starNumber: filters.starNumber } },
            ],
          },
        },
      });
      const hits = response.data.hits.hits;
      setResults(hits);
      setNotFound(hits.length === 0);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  useEffect(() => {
    if (query) {
      fetchResults();
    }
  }, [filters]); // Aplicar filtros automáticamente al seleccionar una opción

  const handleResultClick = (restaurant) => {
    const restaurantName = restaurant._source.name.replace(/\s+/g, '-').toLowerCase();
    navigate(`/${restaurantName}`, { state: { restaurant: restaurant._source } });
  };

  return (
    <div className="App">
      <img src={logo} alt="Logo" className="App-logo" />
      <form onSubmit={handleSearch} className="form">
        <input
          type="text"
          placeholder="Search for restaurants..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          className="input"
        />
        <button type="submit" className="button">Search</button>
      </form>

      <div className="filters">
        <div className="filterGroup">
          <div className="filter">
            <label>
              <input
                type="radio"
                name="starsOrSoles"
                value="both"
                checked={filters.starsOrSoles === 'both'}
                onChange={(e) => setFilters({ ...filters, starsOrSoles: e.target.value })}
              />
              Ambos
            </label>
            <label>
              <input
                type="radio"
                name="starsOrSoles"
                value="estrellas"
                onChange={(e) => setFilters({ ...filters, starsOrSoles: e.target.value })}
              />
              Solo Estrellas
            </label>
            <label>
              <input
                type="radio"
                name="starsOrSoles"
                value="soles"
                onChange={(e) => setFilters({ ...filters, starsOrSoles: e.target.value })}
              />
              Solo Soles
            </label>
          </div>
          <div className="filter">
            <label>Comunidad:</label>
            <select
              value={filters.community}
              onChange={(e) => setFilters({ ...filters, community: e.target.value })}>
              <option value="">Seleccionar comunidad</option>
              {/* Opciones aquí */}
            </select>
          </div>
          <div className="filter">
            <label>Rango de Precio:</label>
            <select
              value={filters.priceRange}
              onChange={(e) => setFilters({ ...filters, priceRange: e.target.value })}>
              <option value="">Seleccionar rango</option>
              {/* Opciones aquí */}
            </select>
          </div>
          <div className="filter">
            <label>Tipo de Comida:</label>
            <select
              value={filters.mealType}
              onChange={(e) => setFilters({ ...filters, mealType: e.target.value })}>
              <option value="">Seleccionar tipo</option>
              {/* Opciones aquí */}
            </select>
          </div>
          <div className="filter">
            <label>Número de Estrellas/Soles:</label>
            <select
              value={filters.starNumber}
              onChange={(e) => setFilters({ ...filters, starNumber: e.target.value })}>
              <option value="">Seleccionar número</option>
              {/* Opciones aquí */}
            </select>
          </div>
        </div>
      </div>

      {notFound && (
        <p className="notFoundMessage">No se encontraron resultados</p>
      )}

      <div className="resultsGrid">
        {results.map((result) => (
          <div
            key={result._id}
            className="resultCard"
            onClick={() => handleResultClick(result)}
            style={{ cursor: 'pointer' }}
          >
            <img
              src={result._source.restaurant_photo_url}
              alt={result._source.name}
              className="restaurantPhoto"
            />
            <div className="resultInfo">
              <h2 className="resultName">{result._source.name}</h2>
              <p className="resultLocation">Madrid, España</p>
              <p className="resultPrice">{result._source.price}</p>
              <p className="resultMealType">{result._source.meal_type}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

function AppWrapper() {
  return (
    <Routes>
      <Route path="/" element={<App />} />
      <Route path="/:restaurantName" element={<RestaurantDetails />} />
    </Routes>
  );
}

export default AppWrapper;
