import React, { useState, useEffect, useCallback } from 'react';
import { Routes, Route, useNavigate, useLocation } from 'react-router-dom';
import { fetchResultsFromAPI } from './services/api';
import './App.css';
import logo from './assets/Logo.png';
import RestaurantDetails from './components/restaurantDetails/RestaurantDetails';
import FilterSection from './components/filterSection/FilterSection';
import ResultCard from './components/resultCard/ResultCard';

function App() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [filteredResults, setFilteredResults] = useState([]);
  const [notFound, setNotFound] = useState(false);
  const [filters, setFilters] = useState({
    starsOrSoles: 'both',
    priceRange: '',
    mealType: '',
    starNumber: '',
  });
  const [filterOptions, setFilterOptions] = useState({
    priceRanges: [],
    mealTypes: []
  });
  const [showFilters, setShowFilters] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();

  // Manejo de búsqueda
  const handleSearch = async (e) => {
    e.preventDefault();
    await fetchResults(query);
  };

  const fetchResults = useCallback(async (searchQuery) => {
    if (!searchQuery) return;

    try {
      const { processedHits, uniqueMealTypes, priceRanges } = await fetchResultsFromAPI(searchQuery);

      setResults(processedHits);
      setFilteredResults(processedHits);
      setNotFound(processedHits.length === 0);

      setFilterOptions({
        priceRanges: priceRanges.sort((a, b) => a.length - b.length),
        mealTypes: uniqueMealTypes.sort(),
      });

    } catch (error) {
      console.error("Error fetching data:", error);
    }
  }, []);

  useEffect(() => {
    if (location.state && location.state.query) {
      setQuery(location.state.query);
      fetchResults(location.state.query);
    }
  }, [location.state, fetchResults]);

  const handleResultClick = (restaurant) => {
    const restaurantName = restaurant._source.name.replace(/\s+/g, '-').toLowerCase();
    navigate(`/${restaurantName}`, { state: { query, restaurant: restaurant._source } });
  };

  const applyFilters = () => {
    let filtered = results;

    if (filters.starsOrSoles === 'estrellas') {
      filtered = filtered.filter((result) => result._source.star_number > 0);
    } else if (filters.starsOrSoles === 'soles') {
      filtered = filtered.filter((result) => result._source.soles_number > 0);
    }

    if (filters.priceRange) {
      filtered = filtered.filter((result) => result._source.price === filters.priceRange);
    }

    if (filters.mealType) {
      filtered = filtered.filter((result) => result._source.meal_type.includes(filters.mealType));
    }

    if (filters.starNumber) {
      filtered = filtered.filter((result) => {
        const starNum = parseInt(filters.starNumber);
        const stars = result._source.star_number || 0;
        const soles = result._source.soles_number || 0;
        return (stars === starNum && soles === 0) || (soles === starNum && stars === 0);
      });
    }

    setFilteredResults(filtered);
  };

  const clearFilters = () => {
    setFilters({
      starsOrSoles: 'both',
      priceRange: '',
      mealType: '',
      starNumber: '',
    });
    setFilteredResults(results);
  };

  const resetState = () => {
    setQuery('');
    setResults([]);
    setFilteredResults([]);
    setNotFound(false);
    setFilters({
      starsOrSoles: 'both',
      priceRange: '',
      mealType: '',
      starNumber: '',
    });
    setFilterOptions({
      priceRanges: [],
      mealTypes: [],
    });
  };

  return (
    <div className="App">
      <img src={logo} alt="Logo" className="App-logo" onClick={resetState} />
      <form onSubmit={handleSearch} className="form">
        <input
          type="text"
          placeholder="Search for restaurants..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <button type="submit">Buscar</button>
        <button type="button" onClick={() => setShowFilters(!showFilters)}>
          Filtros
        </button>
      </form>

      {showFilters && (
        <FilterSection
          filters={filters}
          setFilters={setFilters}
          filterOptions={filterOptions}
          clearFilters={clearFilters}
          applyFilters={applyFilters}
        />
      )}

      {notFound && <p>No se encontraron resultados para "{query}".</p>}

      <div className="resultsGrid">
        {filteredResults.map((result) => (
          <ResultCard
            key={result._id}
            result={result}
            onClick={() => handleResultClick(result)}
          />
        ))}
      </div>
    </div>
  );
}

function AppWrapper() {
  return (
    <Routes>
      <Route path="/" element={<App />} />
      <Route path="/:restaurantName" element={<RestaurantDetails />} />
    </Routes>
  );
}

export default AppWrapper;
