import React from 'react';
import './Filters.css'; // Importa los estilos del componente Filters
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrash } from '@fortawesome/free-solid-svg-icons';
import { faFilter } from '@fortawesome/free-solid-svg-icons';

const Filters = ({ filters, setFilters, filterOptions, showFilters }) => {
  const clearFilters = () => {
    setFilters({
      starsOrSoles: 'both',
      priceRange: '',
      mealType: '', 
      starNumber: '',
    });
  };

  const applyFilters = () => {
    // Apply filtering logic
  };

  return (
    showFilters && (
      <div className="filters">
        <div className="filterGroup">
          {/* Filter Options */}
          <select value={filters.starsOrSoles} onChange={(e) => setFilters({ ...filters, starsOrSoles: e.target.value })}>
            <option value="both">Tipo de galardon</option>
            <option value="estrellas">Estrellas</option>
            <option value="soles">Soles</option>
          </select>
          <select value={filters.priceRange} onChange={(e) => setFilters({ ...filters, priceRange: e.target.value })}>
            <option value="">Rango de precios</option>
            {filterOptions.priceRanges.map((price, index) => (
              <option key={index} value={price}>{price}</option>
            ))}
          </select>
          <select value={filters.mealType} onChange={(e) => setFilters({ ...filters, mealType: e.target.value })}>
            <option value="">Tipo de comida</option>
            {filterOptions.mealTypes.map((mealType, index) => (
              <option key={index} value={mealType}>{mealType}</option>
            ))}
          </select>
          <select value={filters.starNumber} onChange={(e) => setFilters({ ...filters, starNumber: e.target.value })}>
            <option value="">NÃºmero de galardones</option>
            <option value="1">1</option>
            <option value="2">2</option>
            <option value="3">3</option>
          </select>
          <button onClick={clearFilters}>
            <FontAwesomeIcon icon={faTrash} size="xs" />
          </button>
          <button onClick={applyFilters}>Aplicar Filtros</button>
        </div>
      </div>
    )
  );
};

export default Filters;
