import React, { useState } from 'react';
import { Routes, Route, useNavigate } from 'react-router-dom';
import axios from 'axios';
import logo from './Dineology_logo.jpeg';
import RestaurantDetails from './components/RestaurantDetails';

function App() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [notFound, setNotFound] = useState(false);
  const navigate = useNavigate();

  const handleSearch = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:9200/restaurants/_search', {
        query: {
          multi_match: {
            query: query,
            fields: ['name', 'meal_type'],
          },
        },
      });
      const hits = response.data.hits.hits;
      setResults(hits);
      setNotFound(hits.length === 0);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  return (
    <div className="App">
      <img src={logo} alt="Logo" className="App-logo" />
      <form onSubmit={handleSearch} className="form">
        <input
          type="text"
          placeholder="Search for restaurants..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          className="input"
        />
        <button type="submit" className="button">Search</button>
      </form>

      {notFound && (
        <p className="notFoundMessage">Not found</p>
      )}

      <div className="resultsGrid">
        {results.map((result) => (
          <div key={result._id} className="resultCard">
            <img
              src={result._source.restaurant_photo_url}
              alt={result._source.name}
              className="restaurantPhoto"
            />
            <div className="resultInfo">
              <h2 className="resultName">{result._source.name}</h2>
              <p className="resultLocation">Madrid, Espa√±a</p>
              <p className="resultPrice">{result._source.price}</p>
              <p className="resultMealType">{result._source.meal_type}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
