// components/RestaurantDetails.js
import React from 'react';
import { useLocation, useParams, useNavigate } from 'react-router-dom';

function RestaurantDetails() {
  const location = useLocation();
  const { restaurantName } = useParams(); // Obtiene el nombre del restaurante de la URL
  const restaurant = location.state?.restaurant;
  const previousSearch = location.state?.previousSearch; // Obtener la búsqueda anterior
  const navigate = useNavigate();

  if (!restaurant) {
    return (
      <div>
        <p>Datos del restaurante no disponibles.</p>
        <button onClick={() => navigate(-1)} className="button">Volver a la búsqueda</button>
      </div>
    );
  }

  return (
    <div className="detailsContainer">
      <button onClick={() => navigate(-1)} className="button backButton">Volver a la búsqueda</button>
      <h1 className="detailsTitle">{restaurant.name}</h1>
      <img
        src={restaurant.restaurant_photo_url || "default-image.jpg"}
        alt={restaurant.name}
        className="restaurantPhoto"
      />
      <p className="detailsDescription">{restaurant.description || "No hay descripción disponible"}</p>
      <p className="detailsPrice">Precio: {restaurant.price}</p>
      <p className="detailsMealType">Tipo de comida: {restaurant.meal_type || "No disponible"}</p>
      <p className="detailsContact">Contacto: {restaurant.contact_number || "No disponible"}</p>
      <p className="detailsWebUrl">
        <a href={restaurant.web_url} target="_blank" rel="noopener noreferrer">Visitar sitio web</a>
      </p>
      <div className="workingSchedule">
        <h2>Horario de trabajo</h2>
        <ul>
          {Object.entries(restaurant.working_schedule || {}).map(([day, hours]) => (
            <li key={day}>{day}: {hours[0]}</li> // Muestra "cerrado" si no hay horas
          ))}
        </ul>
      </div>
    </div>
  );
}

export default RestaurantDetails;
