import React from 'react';

const MapDisplay = ({ coordinates, loading }) => {
  if (loading) {
    return <p>Cargando mapa...</p>;
  }

  if (!coordinates) {
    return <p>Mapa no disponible en estos momentos.</p>;
  }

  const { lat, lon } = coordinates;
  const mapSrc = `https://www.openstreetmap.org/export/embed.html?bbox=${lon - 0.01},${lat - 0.01},${lon + 0.01},${lat + 0.01}&layer=mapnik&marker=${lat},${lon}`;

  return (
    <iframe
      src={mapSrc}
      width="100%"
      height="600px"
      style={{ border: 0 }}
      allowFullScreen=""
      loading="lazy"
      title="Mapa"
    />
  );
};

export default MapDisplay;
