import React from 'react';

const FilterSection = ({ filters, setFilters, filterOptions, clearFilters, applyFilters }) => {
  return (
    <div className="filters">
      <label>
        Estrellas/Soles:
        <select
          value={filters.starsOrSoles}
          onChange={(e) => setFilters({ ...filters, starsOrSoles: e.target.value })}
        >
          <option value="both">Ambos</option>
          <option value="estrellas">Estrellas</option>
          <option value="soles">Soles</option>
        </select>
      </label>

      <label>
        Rango de precio:
        <select
          value={filters.priceRange}
          onChange={(e) => setFilters({ ...filters, priceRange: e.target.value })}
        >
          <option value="">Todos</option>
          {filterOptions.priceRanges.map((range, idx) => (
            <option key={idx} value={range}>
              {range}
            </option>
          ))}
        </select>
      </label>

      <label>
        Tipo de comida:
        <select
          value={filters.mealType}
          onChange={(e) => setFilters({ ...filters, mealType: e.target.value })}
        >
          <option value="">Todos</option>
          {filterOptions.mealTypes.map((type, idx) => (
            <option key={idx} value={type}>
              {type}
            </option>
          ))}
        </select>
      </label>

      <label>
        NÃºmero de estrellas:
        <select
          value={filters.starNumber}
          onChange={(e) => setFilters({ ...filters, starNumber: e.target.value })}
        >
          <option value="">Todos</option>
          {[1, 2, 3, 4, 5].map((num) => (
            <option key={num} value={num}>
              {num} estrellas
            </option>
          ))}
        </select>
      </label>

      <div>
        <button type="button" onClick={applyFilters}>Aplicar filtros</button>
        <button type="button" onClick={clearFilters}>Limpiar filtros</button>
      </div>
    </div>
  );
};

export default FilterSection;
