const formatMealType = (mealType) => {
  if (Array.isArray(mealType)) {
    return mealType.join(', ');
  }
  return mealType;
};

const processMealTypes = (mealTypes) => {
  if (Array.isArray(mealTypes)) {
    return mealTypes.map((type) => type.toLowerCase());
  }
  return [mealTypes.toLowerCase()];
};

const renderStarsAndSoles = (soles, stars) => {
  const solesText = soles > 0 ? `${soles} soles` : '';
  const starsText = stars > 0 ? `${stars} estrellas` : '';
  return `${solesText}${soles > 0 && stars > 0 ? ' | ' : ''}${starsText}`;
};

export { formatMealType, processMealTypes, renderStarsAndSoles };
