import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [error, setError] = useState(null); // Agregamos un estado para manejar errores

  const handleSearch = async (e) => {
    e.preventDefault();
    setError(null); // Reseteamos el error al iniciar la búsqueda

    try {
      const response = await axios.post('http://localhost:9200/restaurants/_search', {
        query: {
          multi_match: {
            query: query,
            fields: ['name', 'meal_type'] // Solo buscamos en name y meal_type
          }
        }
      });

      // Verificamos si hay resultados
      if (response.data.hits.hits.length === 0) {
        setError('No se encontraron resultados.');
      } else {
        setResults(response.data.hits.hits);
      }
    } catch (err) {
      setError('Error al realizar la búsqueda.'); // Manejo de errores
      console.error(err);
    }
  };

  return (
    <div className="App">
      <form onSubmit={handleSearch}>
        <input
          type="text"
          placeholder="Buscar restaurantes..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <button type="submit">Buscar</button>
      </form>

      {error && <p style={{ color: 'red' }}>{error}</p>} {/* Mostramos el error si existe */}

      <ul>
        {results.map((result) => (
          <li key={result._id}>
            <h2>{result._source.name}</h2>
            <p>{result._source.meal_type}</p>
            <p>{result._source.price}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
