// App.js
import React, { useEffect, useState } from 'react';
import { Routes, Route, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './App.css';
import logo from './Dineology_logo.jpeg';
import RestaurantDetails from './components/RestaurantDetails';
import { useSearch } from './SearchContext'; // Importa el contexto

function App() {
  const { query, setQuery } = useSearch(); // Usa el contexto para obtener query y setQuery
  const [results, setResults] = useState([]);
  const [notFound, setNotFound] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    // Solo ejecuta la búsqueda si hay una consulta
    if (query) {
      const fetchData = async () => {
        try {
          const response = await axios.post('http://localhost:9200/restaurants/_search', {
            query: {
              multi_match: {
                query: query,
                fields: ['name', 'meal_type'],
              },
            },
          });
          const hits = response.data.hits.hits;
          setResults(hits);
          setNotFound(hits.length === 0);
        } catch (error) {
          console.error("Error fetching data:", error);
        }
      };
      fetchData();
    } else {
      // Si no hay consulta, limpia los resultados
      setResults([]);
      setNotFound(false);
    }
  }, [query]); // Ejecutar cuando `query` cambia

  const handleSearch = (e) => {
    e.preventDefault();
    // No necesitas ejecutar la búsqueda aquí, ya que lo hacemos con el useEffect
  };

  const handleResultClick = (restaurant) => {
    // Convert restaurant name to a URL-friendly format
    const restaurantName = restaurant._source.name.replace(/\s+/g, '-').toLowerCase();
    navigate(`/${restaurantName}`, {
      state: {
        restaurant: restaurant._source,
        previousSearch: query // Pasa la consulta anterior
      }
    });
  };

  return (
    <div className="App">
      <img src={logo} alt="Logo" className="App-logo" />
      <form onSubmit={handleSearch} className="form">
        <input
          type="text"
          placeholder="Search for restaurants..."
          value={query}
          onChange={(e) => setQuery(e.target.value)} // Actualiza el contexto
          className="input"
        />
        <button type="submit" className="button">Search</button>
      </form>

      {notFound && (
        <p className="notFoundMessage">Not found</p>
      )}

      <div className="resultsGrid">
        {results.map((result) => (
          <div
            key={result._id}
            className="resultCard"
            onClick={() => handleResultClick(result)}
            style={{ cursor: 'pointer' }}
          >
            <img
              src={result._source.restaurant_photo_url}
              alt={result._source.name}
              className="restaurantPhoto"
            />
            <div className="resultInfo">
              <h2 className="resultName">{result._source.name}</h2>
              <p className="resultLocation">Madrid, España</p>
              <p className="resultPrice">{result._source.price}</p>
              <p className="resultMealType">{result._source.meal_type}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

function AppWrapper() {
  return (
    <Routes>
      <Route path="/" element={<App />} />
      <Route path="/:restaurantName" element={<RestaurantDetails />} />
    </Routes>
  );
}

export default AppWrapper;
